mapper.insert:
		INSERT INTO
		       USER_DETAIL(ID,
		                   PASSWORD
		                  ) 
		           VALUES (#{id},
		                  HEX(AES_ENCRYPT(#{pwd}, (SELECT OBJECT
  												    FROM abworld.COMMON
 												   WHERE TITLE = 'USER'
   													 AND SUBTITLE = 'LOGIN'
   												     AND ID = 'CIPHERS'))
   							 )
   						  );
